INSERT INTO reading_plan_categories (name, description) VALUES
('spiritual_growth', 'Plans focused on spiritual development and deeper faith'),
('biblical_literacy', 'Plans designed to improve Bible knowledge and understanding'),
('topical_studies', 'Plans exploring specific themes and topics in Scripture')
INSERT INTO reading_plans (name, description, duration, difficulty, category, type, created_by, created_at) VALUES
('30-Day Psalms Journey', 'Discover peace and hope through the beautiful poetry of the Psalms. This 30-day journey will guide you through selected Psalms that speak to the heart of faith, trust, and God''s faithfulness.', 30, 'beginner', 'spiritual_growth', 'topical', 'system', NOW()),
('Gospel Foundations', 'Build a solid foundation in your Christian faith by exploring the life and teachings of Jesus Christ through all four Gospels. Perfect for new believers and those wanting to rediscover Jesus.', 60, 'beginner', 'biblical_literacy', 'book_study', 'system', NOW()),
('Wisdom Literature Deep Dive', 'Gain practical wisdom for daily living through an in-depth study of Proverbs, Ecclesiastes, and Job. Learn timeless principles for making godly decisions.', 45, 'intermediate', 'biblical_literacy', 'book_study', 'system', NOW()),
('Peace in Uncertain Times', 'Find comfort and strength during difficult seasons through Scripture that speaks to anxiety, fear, and uncertainty. Discover God''s promises of peace and hope.', 21, 'beginner', 'spiritual_growth', 'topical', 'system', NOW()),
('Heroes of Faith', 'Be inspired by the incredible faith journeys of biblical heroes like Abraham, Moses, David, Esther, and Paul. Learn from their victories and failures.', 35, 'intermediate', 'spiritual_growth', 'topical', 'system', NOW()),
('New Testament Survey', 'Get the big picture of the New Testament through a comprehensive overview of all 27 books. Perfect for understanding the flow of early Christianity.', 90, 'intermediate', 'biblical_literacy', 'chronological', 'system', NOW()),
('Prayer & Worship', 'Deepen your prayer life and worship experience through Scripture that teaches us how to commune with God and live a life of praise.', 28, 'beginner', 'spiritual_growth', 'devotional', 'system', NOW());
INSERT INTO reading_plan_days (plan_id, day_number, title, scripture_reference, scripture_text, devotional_content, reflection_question, prayer_prompt) VALUES
((SELECT id FROM reading_plans WHERE name = '30-Day Psalms Journey'), 1, 'God''s Unfailing Love', 'Psalm 136:1-3', 'Give thanks to the Lord, for he is good. His love endures forever. Give thanks to the God of gods. His love endures forever. Give thanks to the Lord of lords: His love endures forever.', 'Today we begin our journey through the Psalms by recognizing the foundation of our faith: God''s unfailing love. No matter what circumstances you face, God''s love for you remains constant and eternal. This love is not based on your performance or perfection, but on His character and faithfulness.', 'How have you experienced God''s unfailing love in your life, especially during difficult times?', 'Thank God for His endless love and ask Him to help you trust in His love even when circumstances are challenging.'),
INSERT INTO reading_plan_days (plan_id, day_number, title, scripture_reference, scripture_text, devotional_content, reflection_question, prayer_prompt) VALUES
((SELECT id FROM reading_plans WHERE name = 'Peace in Uncertain Times'), 1, 'Do Not Be Anxious', 'Matthew 6:25-27', 'Therefore I tell you, do not worry about your life, what you will eat or drink; or about your body, what you will wear. Is not life more than food, and the body more than clothes? Look at the birds of the air; they do not sow or reap or store away in barns, and yet your heavenly Father feeds them. Are you not much more valuable than they? Can any one of you by worrying add a single hour to your life?', 'Jesus'' words remind us that worry is not only unproductive but also unnecessary. Our heavenly Father, who cares for the birds of the air, surely cares for us. When we find ourselves consumed by anxiety about the future, we can choose to trust in God''s provision and care.', 'What worries are you carrying today that you need to surrender to God?', 'Ask God to help you release your anxieties and trust in His loving care and provision.'),
INSERT INTO reading_plan_days (plan_id, day_number, title, scripture_reference, devotional_content, reflection_question, prayer_prompt) VALUES
((SELECT id FROM reading_plans WHERE name = 'Gospel Foundations'), 1, 'The Word Became Flesh', 'John 1:1-14', 'Begin your journey into the Gospels by understanding who Jesus is - the eternal Word of God who became human to dwell among us.', 'What does it mean to you that Jesus, being God, chose to become human?', 'Thank Jesus for leaving heaven to come to earth and ask Him to reveal Himself to you through this study.'),
